import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const FILE_TYPES = [
    ".txt",
    ".py",
    ".js",
    ".sql",
    ".env",
    ".json",
    ".html",
    ".css",
    ".java",
    ".cpp",
    ".c",
    ".php",
    ".rb",
    ".xml",
    ".yml",
    ".md",
    ".sh",
    ".swift",
    ".h",
    ".pyw",
    ".asm",
    ".asp",
    ".aspx",
    ".bat",
    ".cmd",
    ".cls",
    ".coffee",
    ".erb",
    ".go",
    ".groovy",
    ".htaccess",
    ".java",
    ".jsp",
    ".lua",
    ".make",
    ".matlab",
    ".pas",
    ".perl",
    ".pl",
    ".ps1",
    ".r",
    ".scala",
    ".scm",
    ".sln",
    ".vb",
    ".vbs",
    ".xhtml",
    ".xsl",
  ];
  const [repoUrl, setRepoUrl] = useState("");
  const [docUrl, setDocUrl] = useState("");
  const [response, setResponse] = useState("");
  const [selectedFileTypes, setSelectedFileTypes] = useState([]);
  const [fileSelection, setFileSelection] = useState("all");

  const handleRepoChange = (e) => {
    setRepoUrl(e.target.value);
  };

  const handleDocChange = (e) => {
    setDocUrl(e.target.value);
  };

  const handleFileTypeChange = (e) => {
    if (e.target.checked) {
      setSelectedFileTypes([...selectedFileTypes, e.target.value]);
    } else {
      setSelectedFileTypes(
        selectedFileTypes.filter((fileType) => fileType !== e.target.value)
      );
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const result = await axios.post("http://localhost:5000/scrape", {
        repoUrl,
        docUrl,
        selectedFileTypes,
      });
      setResponse(result.data.response);
    } catch (error) {
      console.error(error);
    }
  };

  const handleCopyText = () => {
    const outputArea = document.querySelector(".outputArea");
    outputArea.select();
    document.execCommand("copy");
  };

  const handleFileSelectionChange = (e) => {
    setFileSelection(e.target.value);
  };

  return (
    <div className="container">
      {/* ... your other elements ... */}
      <div className="fileSelectionContainer">
        <div>
          <input
            type="radio"
            value="all"
            checked={fileSelection === "all"}
            onChange={handleFileSelectionChange}
          />
          <label>All Files</label>
        </div>
        <div>
          <input
            type="radio"
            value="select"
            checked={fileSelection === "select"}
            onChange={handleFileSelectionChange}
          />
          <label>Select File Types</label>
        </div>
      </div>
      {fileSelection === "select" && (
        <div className="fileTypesContainer">
          <p>Select file types:</p>
          {FILE_TYPES.map((fileType, index) => (
            <div key={index}>
              <input
                type="checkbox"
                value={fileType}
                onChange={handleFileTypeChange}
              />
              <label>{fileType}</label>
            </div>
          ))}
        </div>
      )}
      {/* ... your other elements ... */}
    </div>
  );
}

export default App;
