import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const FILE_TYPES = [
    ".txt",
    ".py",
    ".js",
    ".sql",
    ".env",
    ".json",
    ".html",
    ".css",
    ".java",
    ".cpp",
    ".c",
    ".php",
    ".rb",
    ".xml",
    ".yml",
    ".md",
    ".sh",
    ".swift",
    ".h",
    ".pyw",
    ".asm",
    ".asp",
    ".aspx",
    ".bat",
    ".cmd",
    ".cls",
    ".coffee",
    ".erb",
    ".go",
    ".groovy",
    ".htaccess",
    ".java",
    ".jsp",
    ".lua",
    ".make",
    ".matlab",
    ".pas",
    ".perl",
    ".pl",
    ".ps1",
    ".r",
    ".scala",
    ".scm",
    ".sln",
    ".vb",
    ".vbs",
    ".xhtml",
    ".xsl",
  ];
  const [repoUrl, setRepoUrl] = useState("");
  const [docUrl, setDocUrl] = useState("");
  const [response, setResponse] = useState("");
  const [selectedFileTypes, setSelectedFileTypes] = useState([]);

  const handleRepoChange = (e) => {
    setRepoUrl(e.target.value);
  };

  const handleDocChange = (e) => {
    setDocUrl(e.target.value);
  };

  const handleFileTypeChange = (e) => {
    if (e.target.checked) {
      setSelectedFileTypes([...selectedFileTypes, e.target.value]);
    } else {
      setSelectedFileTypes(
        selectedFileTypes.filter((fileType) => fileType !== e.target.value)
      );
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const result = await axios.post("http://localhost:5000/scrape", {
        repoUrl,
        docUrl,
        selectedFileTypes,
      });
      setResponse(result.data.response);
    } catch (error) {
      console.error(error);
    }
  };

  const handleCopyText = () => {
    const outputArea = document.querySelector(".outputArea");
    outputArea.select();
    document.execCommand("copy");
  };

  return (
    <div className="container">
      <div className="inputContainer">
        <input
          value={repoUrl}
          onChange={handleRepoChange}
          placeholder="Enter Github repo URL"
          className="inputArea"
        />
        <input
          value={docUrl}
          onChange={handleDocChange}
          placeholder="Enter documentation URL (optional)"
          className="inputArea"
        />
        <div className="fileTypesContainer">
          <p>Select file types:</p>
          {FILE_TYPES.map((fileType, index) => (
            <div key={index}>
              <input
                type="checkbox"
                value={fileType}
                onChange={handleFileTypeChange}
              />
              <label>{fileType}</label>
            </div>
          ))}
        </div>
      </div>
      <div className="buttonContainer">
        <button onClick={handleSubmit} className="transformButton">
          Submit
        </button>
        <button onClick={handleCopyText} className="copyButton">
          Copy Text
        </button>
      </div>
      <div className="outputContainer">
        <textarea value={response} readOnly className="outputArea" />
      </div>
    </div>
  );
}

export default App;
